["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        allstates[\"\"] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = 6.15,\n            expirationTime = GetTime() + 6.15,\n            assignment = aura_env.assignmentText[math.random(3)],\n            autoHide = true\n        }\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        if not aura_env.note then return end\n        \n        local casterUnit, castGUID, spellID = ...\n        local npcID = LiquidWeakAuras:CreatureInfo(casterUnit)\n        \n        if not castGUID then return end\n        if spellID ~= 1239262 then return end -- Conqueror's Cross\n        if npcID ~= 245222 then return end -- Pargoth\n        \n        aura_env.set = aura_env.set + 1\n        \n        local assignments = CopyTable(aura_env.assignments[aura_env.set] or {})\n        local backups = CopyTable(aura_env.backups)\n        local eligibleGUIDs = {}\n        \n        -- Find eligible GUIDs\n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            local isVisible = UnitIsVisible(unit)\n            local isAlive = not UnitIsDeadOrGhost(unit)\n            local hasStarshard = WA_GetUnitDebuff(unit, 1254385)\n            \n            if isVisible and isAlive and not hasStarshard then\n                eligibleGUIDs[GUID] = true\n            end\n        end\n        \n        -- Remove ineligible backups\n        for index, GUID in ipairs_reverse(backups) do\n            if not eligibleGUIDs[GUID] then\n                table.remove(backups, index)\n            end\n        end\n        \n        -- Assign backups where necessary\n        for index = 1, 3 do\n            local GUID = assignments[index]\n            local isEligible = GUID and eligibleGUIDs[GUID]\n            \n            if not isEligible then\n                assignments[index] = table.remove(backups, 1) or \"\"\n            end\n        end\n        \n        -- Starshard Nova always happens ~6.15 seconds after Pargoth's Conqueror's Cross cast start\n        for index, GUID in ipairs(assignments) do\n            if index > 3 then break end -- Safety measure in case more than 3 names were put in the note\n            \n            if GUID == WeakAuras.myGUID then\n                local assignment = aura_env.assignmentText[index]\n                \n                allstates[\"\"] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = 6.15,\n                    expirationTime = GetTime() + 6.15,\n                    assignment = assignment,\n                    say = aura_env.say[index],\n                    autoHide = true\n                }\n                \n                if aura_env.config.tts then\n                    LiquidWeakAuras:PlaySound(assignment)\n                end\n            end\n            \n            WeakAuras.ScanEvents(\"LIQUID_STARSHARD_NOVA_ASSIGNMENT\", GUID, index)\n        end\n        \n        return true\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.set = 0\n        \n        aura_env:ReadNote()\n    end\nend",
