["customGrow"] = "function(newPositions, activeRegions)\n    local xMin = math.huge\n    local xMax = -math.huge\n    local yMin = math.huge\n    local yMax = -math.huge\n    \n    for i, regionData in ipairs(activeRegions) do\n        local region = regionData.region\n        local state = region.state\n        \n        if not state.position then return end\n        \n        local position = state.position\n        \n        -- Size override\n        local sizeOverride = state.width\n        \n        if sizeOverride then\n            region:SetRegionWidth(sizeOverride)\n            region:SetRegionHeight(sizeOverride)\n        end\n        \n        xMin = math.min(xMin, position[1] - 0.5 * region.width)\n        xMax = math.max(xMax, position[1] + 0.5 * region.width)\n        \n        yMin = math.min(yMin, position[2] - 0.5 * region.height)\n        yMax = math.max(yMax, position[2] + 0.5 * region.height)\n        \n        newPositions[i] = {position[1], position[2]}\n    end\n    \n    -- Center the graphic\n    local xOffset = 0.5 * (xMax - math.abs(xMin))\n    local yOffset = 0.5 * (yMax - math.abs(yMin))\n    \n    for _, position in ipairs(newPositions) do\n        position[1] = position[1] - xOffset\n        position[2] = position[2] - yOffset\n    end\nend",
