["init"] = {
["custom"] = "local INVERSE_SIZE = WeakAuras.GetData(aura_env.id).width\nlocal DURATION = 5\n\n-- 1: melee\n-- 2: healers\n-- 3: ranged\nlocal INVERSE_POSITIONS = {\n    [1] = {\n        {\n            {0, -1.1 * INVERSE_SIZE, \"Bottom\"},\n            {0, 0, \"Middle\"},\n            {0, 1.1 * INVERSE_SIZE, \"Top\"},\n        },\n        {\n            {-0.5 * INVERSE_SIZE, -0.55 * INVERSE_SIZE, \"Middle\"},\n            {0.55 * INVERSE_SIZE, -0.5 * INVERSE_SIZE, \"Right\"},\n            {-0.5 * INVERSE_SIZE, 0.55 * INVERSE_SIZE, \"Top\"},\n        },\n    },\n    [2] = {\n        {\n            {-0.45 * INVERSE_SIZE, -0.6 * INVERSE_SIZE, \"Bottom\"},\n            {0.45 * INVERSE_SIZE, 0, \"Middle\"},\n            {-0.45 * INVERSE_SIZE, 0.6 * INVERSE_SIZE, \"Top\"},\n        },\n        {\n            {0, -1.1 * INVERSE_SIZE, \"Bottom\"},\n            {0, 0, \"Middle\"},\n            {0, 1.1 * INVERSE_SIZE, \"Top\"},\n        },\n    },\n    [3] = {\n        {\n            {1.1 * INVERSE_SIZE, 0.15 * INVERSE_SIZE, \"Right\"},\n            {0, -0.1 * INVERSE_SIZE, \"Middle\"},\n            {-1.1 * INVERSE_SIZE, 0.15 * INVERSE_SIZE, \"Left\"},\n            \n        },\n        {\n            {0.6 * INVERSE_SIZE, 0.6 * INVERSE_SIZE, \"Right\"},\n            {0, -0.6 * INVERSE_SIZE, \"Middle\"},\n            {-0.6 * INVERSE_SIZE, 0.6 * INVERSE_SIZE, \"Left\"},\n            \n        },\n        {\n            {0.6 * INVERSE_SIZE, 0.6 * INVERSE_SIZE, \"Right\"},\n            {0, -0.6 * INVERSE_SIZE, \"Middle\"},\n            {-0.6 * INVERSE_SIZE, 0.6 * INVERSE_SIZE, \"Left\"},\n            \n        },\n        {\n            {1.6 * INVERSE_SIZE, 0.15 * INVERSE_SIZE, \"Right\"},\n            {0, -0.1 * INVERSE_SIZE, \"Middle\"},\n            {-1.6 * INVERSE_SIZE, 0.15 * INVERSE_SIZE, \"Left\"},\n            \n        },\n        {\n            {1.6 * INVERSE_SIZE, 0.15 * INVERSE_SIZE, \"Right\"},\n            {0, -0.1 * INVERSE_SIZE, \"Middle\"},\n            {-1.6 * INVERSE_SIZE, 0.15 * INVERSE_SIZE, \"Left\"},\n        }\n    },\n}\n\nfunction aura_env:CreateStates(allstates, isInverse, phase, set)\n    local myIndex = aura_env.crushingAssignment\n    \n    -- If the player is affected by Inverse Gravity, decide index based on their role/range\n    if isInverse then\n        local rangeType = LiquidWeakAuras:Spec(\"player\")\n        local isHealer = UnitGroupRolesAssigned(\"player\") == \"HEALER\"\n        \n        if isHealer then\n            myIndex = 2\n        else\n            if rangeType == \"m\" then -- Melee\n                myIndex = 1\n            elseif rangeType == \"r\" then -- Ranged\n                myIndex = 3\n            end\n        end\n    end\n    \n    if not myIndex then return end\n    \n    local inversePositions = INVERSE_POSITIONS[phase][set]\n    \n    for index, position in ipairs(inversePositions) do\n        local text = inversePositions[index][3]\n        local assigned = index == myIndex\n        \n        allstates[index] = {\n            show = true,\n            changed = true,\n            progressType = \"timed\",\n            duration = DURATION,\n            expirationTime = GetTime() + DURATION,\n            index = index,\n            position = position,\n            assigned = assigned,\n            isInverse = isInverse,\n            say = isInverse and assigned and text,\n            text = assigned and text,\n            autoHide = true\n        }\n    end\nend\n\nfunction aura_env:ReadNote()\n    aura_env.crushingAssignment = nil\n    \n    for lineCount, line in LiquidWeakAuras:IterateNoteAssignment(\"Crushing\") do\n        if lineCount > 3 then return end\n        \n        local _, containsPlayerGUID = LiquidWeakAuras:LineToGUIDArray(line)\n        \n        if containsPlayerGUID then\n            aura_env.crushingAssignment = lineCount\n            \n            break\n        end\n    end\nend",
