["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        aura_env.crushingAssignment = math.random(3)\n        \n        aura_env:CreateStates(allstates, math.random(2) == 1, math.random(3), math.random(2))\n    elseif event == \"LIQUID_ASSIGNMENT_EVENT\" then\n        local id = ...\n        \n        if id ~= aura_env.id then return end\n        \n        aura_env.set = aura_env.set + 1\n        aura_env.affected = {}\n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        if aura_env.disabled then return end\n        \n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" then\n            if spellID == 1234244 then -- Inverse Gravity\n                -- This is just here to increase the set count\n                table.insert(aura_env.affected, destGUID)\n                \n                if #aura_env.affected == 1 then\n                    local id = aura_env.id\n                    \n                    C_Timer.After(1, function() WeakAuras.ScanEvents(\"LIQUID_ASSIGNMENT_EVENT\", id) end)\n                end\n                \n                if destGUID == WeakAuras.myGUID then\n                    aura_env:CreateStates(allstates, true, aura_env.phase, aura_env.set)\n                    \n                    return true\n                end\n            elseif spellID == 1234243 then -- Crushing Gravity\n                if not aura_env.crushingAssignment then return end\n                \n                if destGUID == WeakAuras.myGUID then\n                    aura_env:CreateStates(allstates, false, aura_env.phase, aura_env.set)\n                    \n                    return true\n                end\n            elseif spellID == 1246143 then -- Touch of Oblivion\n                aura_env.affected = {}\n                aura_env.phase = aura_env.phase + 1\n                aura_env.set = 1\n            end\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1231716 then return end -- Extinguish the Stars\n        \n        aura_env.affected = {}\n        aura_env.phase = 3\n        aura_env.set = 1\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.disabled = LiquidWeakAuras:IsAssignmentsDisabled()\n        \n        aura_env.affected = {}\n        aura_env.phase = 0\n        aura_env.set = 1\n        \n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            aura_env.GUIDToUnit[UnitGUID(unit)] = unit\n        end\n        \n        aura_env:ReadNote()\n    end\nend",
