},
["customText"] = "function()\n    local percentage = aura_env.state and aura_env.state.percentage\n    local averageHealthFraction = aura_env.state and aura_env.state.averageHealthFraction\n    \n    if percentage and averageHealthFraction then\n        local redValue = -10 * averageHealthFraction\n        \n        percentage = Clamp(percentage, redValue, 0)\n        \n        local fraction = percentage / redValue\n        \n        local hueDifference = 0.33 * fraction\n        local h = 0.33 - hueDifference\n        local r, g, b = aura_env.hsvToRgb(h, 0.9, 1)\n        \n        aura_env.region:Color(r, g, b, 1)\n    end\nend",
["yOffset"] = -25,
["anchorPoint"] = "BOTTOM",
["actions"] = {
["start"] = {
},
["finish"] = {
},
["init"] = {
["do_custom"] = true,
["custom"] = "-- From https://github.com/EmmanuelOga/columns/blob/master/utils/color.lua\nfunction aura_env.hsvToRgb(h, s, v)\n    local r, g, b\n    \n    local i = math.floor(h * 6);\n    local f = h * 6 - i;\n    local p = v * (1 - s);\n    local q = v * (1 - f * s);\n    local t = v * (1 - (1 - f) * s);\n    \n    i = i % 6\n    \n    if i == 0 then r, g, b = v, t, p\n    elseif i == 1 then r, g, b = q, v, p\n    elseif i == 2 then r, g, b = p, v, t\n    elseif i == 3 then r, g, b = p, q, v\n    elseif i == 4 then r, g, b = t, p, v\n    elseif i == 5 then r, g, b = v, p, q\n    end\n    \n    return r, g, b\nend",
},
},
["triggers"] = {
{
["trigger"] = {
["type"] = "custom",
