["custom"] = "\n-- This table is received from the back-end aura\n-- The format of the entries is outlined below\n-- Spells present in the order table will only show up if they're present in this table\n-- cooldownInfo[GUID][spellName] = {\n--     spellID = <number>,\n--     spellIcon = <number>,\n--     duration = <number (possibly 0 if ready)>,\n--     expirationTime = <number (possibly 0 if ready)>,\n--     alive = <bool>\n-- }\nlocal cooldownInfo = {}\n\n-- This table holds the assigned order of spells per the MRT note\n-- It's formatted as follows:\n-- order[set] = {\n--     [1] = {\n--         GUID = <string>,\n--         spellName = <string>\n--     },\n--     [2] = {\n--         GUID = <string>,\n--         spellName = <string>\n--     },\n--     etc.\n-- }\nlocal order = {}\n\nfunction aura_env:ReadNote()\n    order = {}\n    \n    local setNumber\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment(\"CC\") do\n        line = line:gsub(\"||c%x%x%x%x%x%x%x%x\", \"\")\n        line = line:gsub(\"||r\", \"\")\n        \n        local set = line:lower():match(\"^set(%d+)\")\n        \n        -- Match both \"set%d\" and \"set %d\"\n        if not set then\n            set = line:lower():match(\"^set%s+(%d+)\")\n        end\n        \n        set = tonumber(set)\n        \n        if set then\n            setNumber = set\n            \n            if not order[setNumber] then order[setNumber] = {} end\n        elseif setNumber then\n            local unitName, spell = line:match(\"^(.-)%s+(.+)\")\n            local GUID = unitName and LiquidWeakAuras:LineToGUIDArray(unitName)[1]\n            \n            if GUID and spell then\n                -- If the spell was specified by ID, convert it to a name before adding it\n                local spellID = tonumber(spell)\n                \n                if spellID then\n                    local spellInfo = C_Spell.GetSpellInfo(spellID)\n                    \n                    spell = spellInfo.name\n                end\n                \n                -- We want to index spells all lowercase, with apostrophes removed\n                spell = spell:lower():gsub(\"\\'\", \"\")\n                \n                table.insert(\n                    order[setNumber],\n                    {\n                        GUID = GUID,\n                        spellName = spell\n                    }\n                )\n            end\n        end\n    end\nend\n\nfunction aura_env:UpdateCooldownInfo(receivedInfo)\n    cooldownInfo = CopyTable(receivedInfo)\nend\n\nfunction aura_env:NotifyNext()\n    local currentTime = GetTime()\n    local currentOrder = order[aura_env.set]\n    \n    if not currentOrder then return end\n    \n    for _, entry in ipairs(currentOrder) do\n        local GUID = entry.GUID\n        local spellName = entry.spellName\n        local info = cooldownInfo[GUID] and cooldownInfo[GUID][spellName]\n        \n        if info and info.alive then\n            local expirationTime = info.expirationTime\n            local isReady = expirationTime and expirationTime < currentTime + 2\n            \n            if isReady then\n                WeakAuras.ScanEvents(\"LIQUID_NEXT_CC\", GUID, info.spellID, aura_env.set)\n                \n                return\n            end\n        end\n    end\nend\n\nfunction aura_env:BuildStates(allstates)\n    local currentOrder = order[aura_env.set]\n    \n    if not currentOrder then return end -- Only rebuild if entries for this set exist\n    \n    for _, state in pairs(allstates) do\n        state.show = false\n        state.changed = true\n    end\n    \n    -- Check if we are included in the order\n    if not aura_env.config.alwaysShow then\n        local included = false\n        \n        for _, entry in ipairs(currentOrder) do\n            if entry.GUID == WeakAuras.myGUID then\n                included = true\n            end\n        end\n        \n        if not included then return end\n    end\n    \n    local currentTime = GetTime()\n    \n    for index, entry in ipairs(currentOrder) do\n        local GUID = entry.GUID\n        local spellName = entry.spellName\n        local info = cooldownInfo[GUID] and cooldownInfo[GUID][spellName]\n        \n        if info and info.alive then\n            local expirationTime = info.expirationTime\n            local isReady = expirationTime and expirationTime < currentTime + (5 + 2 * index)\n            \n            if isReady then\n                allstates[index] = {\n                    show = true,\n                    changed = true,\n                    progressType = \"timed\",\n                    duration = info.duration,\n                    expirationTime = expirationTime,\n                    index = index,\n                    icon = info.spellIcon,\n                    me = GUID == WeakAuras.myGUID,\n                    GUID = GUID,\n                    autoHide = false\n                }\n            end\n        end\n    end\nend",
},
