["custom"] = "-- Tracked spells as specified by the MRT note, keyed by spell name\n-- Spell name is used instead of spell ID, since some spells change ID based on talents/spec\n-- These are all lower case, and apostrophes are omitted\nlocal trackedSpells = {}\n\n-- Holds information about group members' cooldowns\n-- Only has entries for tracked spells\n-- Each entry is in the format:\n-- cooldownInfo[GUID][spellName] = {\n--     spellID = <number>,\n--     spellIcon = <number>,\n--     duration = <number (possibly 0 if ready)>,\n--     expirationTime = <number (possibly 0 if ready)>,\n--     alive = <bool>\n-- }\nlocal cooldownInfo = {}\n\nlocal LibOpenRaid = LibStub:GetLibrary(\"LibOpenRaid-1.0\", true)\n\n-- Register LibOpenRaid callbacks\nif LibOpenRaid and not aura_env.region.callbacksRegistered then\n    local id = aura_env.id\n    \n    local callbacks = {\n        CooldownListUpdate = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownListUpdate\", id, ...) end,\n        CooldownListWipe = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownListWipe\", id, ...) end,\n        CooldownUpdate = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownUpdate\", id, ...) end,\n        CooldownAdded = function(...) WeakAuras.ScanEvents(\"LIQUID_CooldownAdded\", id, ...) end\n    }\n    \n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownListUpdate\", \"CooldownListUpdate\")\n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownListWipe\", \"CooldownListWipe\")\n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownUpdate\", \"CooldownUpdate\")\n    LibOpenRaid.RegisterCallback(callbacks, \"CooldownAdded\", \"CooldownAdded\")\n    \n    aura_env.region.callbacksRegistered = true\nend\n\nfunction aura_env:UpdateEntry(GUID, spellID, receivedInfo)\n    if not LibOpenRaid then return end\n    if not GUID then return end\n    if not receivedInfo then return end\n    \n    -- Convert to all lowercase spell name with apostrophes removed\n    -- This is how keys are formatted in the cooldownInfo table\n    local spellInfo = C_Spell.GetSpellInfo(spellID)\n    \n    if not spellInfo then return end\n    \n    local spellName = spellInfo and spellInfo.name\n    \n    spellName = spellName:lower():gsub(\"\\'\", \"\")\n    \n    if not trackedSpells[spellName] then return end -- If this spell is not tracked, return\n    if not cooldownInfo[GUID] then cooldownInfo[GUID] = {} end\n    \n    local unit = aura_env.GUIDToUnit[GUID]\n    local alive = not UnitIsDeadOrGhost(unit)\n    \n    local _, _, timeLeft, charges, _, _, _, duration = LibOpenRaid.GetCooldownStatusFromCooldownInfo(receivedInfo)\n    local expirationTime = charges >= 1 and 0 or GetTime() + timeLeft\n    \n    cooldownInfo[GUID][spellName] = {\n        spellID = spellID,\n        spellIcon = spellInfo.iconID,\n        duration = duration,\n        expirationTime = expirationTime,\n        alive = alive\n    }\nend\n\nfunction aura_env:UpdateAllEntries()\n    if not LibOpenRaid then return end\n    \n    -- Wipe all info\n    cooldownInfo = {}\n    \n    -- Update entries\n    local allUnitsCooldown = LibOpenRaid.GetAllUnitsCooldown()\n    \n    if allUnitsCooldown then\n        for unit, unitCooldowns in pairs(allUnitsCooldown) do\n            local GUID = UnitGUID(unit)\n            \n            if GUID then\n                for spellID, receivedInfo in pairs(unitCooldowns) do\n                    aura_env:UpdateEntry(GUID, spellID, receivedInfo)\n                end\n            end\n        end\n    end\n    \n    LibOpenRaid.RequestAllData()\nend\n\nfunction aura_env:AddNoteSpells()\n    if not LibOpenRaid then return end\n    \n    for _, line in LiquidWeakAuras:IterateNoteAssignment(\"CC\") do\n        line = strtrim(line)\n        line = line:gsub(\"\\'\", \"\")\n        \n        local spell = line:match(\"^.-%s+(.+)\")\n        \n        if spell then\n            local spellID = tonumber(spell)\n            \n            -- If the spell was specified by ID, convert it to a name before adding it\n            if spellID then\n                local spellInfo = C_Spell.GetSpellInfo(spellID)\n                \n                spell = spellInfo.name\n            end\n            \n            spell = spell:lower():gsub(\"\\'\", \"\")\n            \n            trackedSpells[spell] = true\n        end\n    end\nend\n\nfunction aura_env:Notify()\n    if not LibOpenRaid then return end\n    \n    WeakAuras.ScanEvents(\"LIQUID_CC_UPDATE\", cooldownInfo)\nend\n\n-- Add Gravity Lapse\nif LibOpenRaid then\n    LIB_OPEN_RAID_COOLDOWNS_INFO[449700] = {\n        cooldown = 40,\n        duration = 3,\n        specs = {62, 63, 64},\n        talent = false,\n        charges = 1,\n        class = \"MAGE\",\n        type = 8\n    }\nend",
},
