["custom"] = "function(event, ...)\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local _, subEvent, _, _, _, _, _, destGUID, _, _, _, spellID = ...\n        \n        if subEvent == \"SPELL_AURA_APPLIED\" then\n            if spellID == 1228206 then -- Excess Mass\n                aura_env.excessMassCount = aura_env.excessMassCount + 1\n                \n                aura_env:Assign()\n            elseif spellID == 1243577 or spellID == 1243609 then -- Reverse Gravity/Airborne\n                aura_env:Assign()\n            end\n        elseif subEvent == \"UNIT_DIED\" and aura_env.GUIDToUnit[destGUID] then\n            aura_env:Assign()\n        end\n    elseif event == \"LIQUID_UNIT_ALIVE\" then\n        aura_env:Assign()\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1230087 then return end\n        \n        -- The reason this is done on UNIT_SPELLCAST_START instead of UNIT_SPELLCAST_SUCCEEDED\n        -- is that the cast seems to frequently get \"canceled\" even though it still actually happens\n        C_Timer.After(\n            7,\n            function()\n                aura_env.excessMassCount = 0\n                aura_env.set = aura_env.set + 1\n                \n                aura_env:Assign()\n            end\n        )\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.set = 1 -- Set of Excess Masses. Initial set (on pull) is #1.\n        aura_env.excessMassCount = 0 -- Number of Excess Masses that have been picked up this set\n        aura_env.GUIDToUnit = {}\n        \n        for unit in WA_IterateGroupMembers() do\n            local GUID = UnitGUID(unit)\n            \n            aura_env.GUIDToUnit[GUID] = unit\n        end\n        \n        aura_env:ReadNote()\n        aura_env:Assign()\n    end\nend",
