["custom"] = "function(event, ...)\n    if event == \"INSTANCE_ENCOUNTER_ENGAGE_UNIT\" then\n        if not aura_env.config.enable then return end\n        if aura_env.timer then return end\n        \n        local aura_env = aura_env\n        \n        aura_env.timer = C_Timer.NewTimer(\n            0.5,\n            function()\n                for i = 1, 12 do\n                    local unit = \"boss\" .. i\n                    \n                    if UnitExists(unit) then\n                        local GUID = UnitGUID(unit)\n                        local npcID = LiquidWeakAuras:CreatureInfo(GUID)\n                        local isLivingMass = npcID == 242587\n                        \n                        if isLivingMass and not aura_env.markedGUIDs[GUID] then\n                            local existingMark = GetRaidTargetIndex(unit)\n                            \n                            aura_env.mark = aura_env.mark + 1\n                            \n                            if existingMark ~= aura_env.mark then\n                                SetRaidTarget(unit, aura_env.mark)\n                            end\n                            \n                            aura_env.markedGUIDs[GUID] = true\n                        end\n                    end\n                end\n            end\n        )\n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local _, castGUID, spellID = ...\n        \n        if not castGUID then return end\n        if spellID ~= 1230087 then return end -- Massive Smash\n        \n        aura_env.mark = 0\n    elseif event == \"ENCOUNTER_START\" then\n        aura_env.mark = 0\n        aura_env.markedGUIDs = {}\n    end\nend",
